import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import SearchBox from './components/SearchBox';
import AddFavourites from './components/AddFavourites';
import RemoveFavourites from './components/RemoveFavourites';
import Auth from './components/Auth';
import UserPage from './components/UserPage';

const App = () => {
	const [movies, setMovies] = useState([]);
	const [favourites, setFavourites] = useState([]);
	const [searchValue, setSearchValue] = useState('');
  const [currentUser, setCurrentUser] = useState({});
  const [dateRented, setDateRented] = useState(getDate());
  const [expiration, setExpiration] = useState(getDate());
  const [rating, setRating] = useState(0)

	const getMovieRequest = async () => {
		const url = `http://localhost:3000/movies`;

		const response = await fetch(url);
		const responseJson = await response.json();

		if (responseJson) {
			setMovies(responseJson);
		}
	};
  // const getFavourites = async () => {
	// 	const url = `http://localhost:3000/users`;

	// 	const response = await fetch(url);
	// 	const responseJson = await response.json();

	// 	if (responseJson) {
	// 		setMovies(responseJson);
	// 	}
	// };

	useEffect(() => {
		getMovieRequest();
	}, []);

	useEffect(() => {
		const movieFavourites = currentUser.movies;

		if (movieFavourites) {
			setFavourites(currentUser.movies);
      console.log("favorites" , favourites)
		}
	}, []);

	const saveToLocalStorage = (items) => {
		localStorage.setItem('react-movie-app-favourites', JSON.stringify(items));
	};

	const addFavouriteMovie = (movie) => {
    if (currentUser)
    e.preventDefault();

    const newFavourite = {
        dateRented,
        expiration,
        price,
        rating,
        movie_id,
        user_id
    }
    fetch(`http://localhost:3000/users`, {
        method: 'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(user)
    })
		const newFavouriteList = [...favourites, movie];
		setFavourites(newFavouriteList);
		saveToLocalStorage(newFavouriteList);
	};

	const removeFavouriteMovie = (movie) => {
		const newFavouriteList = favourites.filter(
			(favourite) => favourite.imdbID !== movie.imdbID
		);

		setFavourites(newFavouriteList);
		saveToLocalStorage(newFavouriteList);
	};

	return (
		<div className='container-fluid movie-app'>
      <p>Current Logged in User is: {currentUser.username}</p>
      <Auth
       setCurrentUser={setCurrentUser} 
      setFavourites={setFavourites}
      />
      <UserPage 
      currentUser={currentUser} 
      />
			<div className='row d-flex align-items-center mt-4 mb-4'>
				<MovieListHeading heading='Movies' />
				<SearchBox searchValue={searchValue} setSearchValue={setSearchValue} />
			</div>

			<div className='row'>
				<MovieList
					movies={movies}
					handleFavouritesClick={addFavouriteMovie}
					favouriteComponent={AddFavourites}
				/>
			</div>
			<div className='row d-flex align-items-center mt-4 mb-4'>
				<MovieListHeading heading='Favourites' />
			</div>
			<div className='row'>
				<MovieList
					movies={favourites}
					handleFavouritesClick={removeFavouriteMovie}
					favouriteComponent={RemoveFavourites}
				/>
			</div>
		</div>
	);
};

export default App;


